async function mineThreeCoalOre(bot) {
  const mcData = require('minecraft-data')(bot.version);

  // Step 1: Locate the nearest coal ore blocks
  let coalOreBlocks = bot.findBlocks({
    matching: mcData.blocksByName["coal_ore"].id,
    maxDistance: 32,
    count: 3
  });

  // Step 2: If not enough coal ore blocks are found, explore until we find them
  if (coalOreBlocks.length < 3) {
    bot.chat("Not enough coal ore nearby, exploring to find more.");
    coalOreBlocks = await exploreUntil(bot, new Vec3(Math.random(), 0, Math.random()), 60, () => {
      return bot.findBlocks({
        matching: mcData.blocksByName["coal_ore"].id,
        maxDistance: 32,
        count: 3
      });
    });
  }

  // Step 3: Equip a stone or iron pickaxe
  const stonePickaxe = bot.inventory.findInventoryItem(mcData.itemsByName["stone_pickaxe"].id);
  const ironPickaxe = bot.inventory.findInventoryItem(mcData.itemsByName["iron_pickaxe"].id);
  if (stonePickaxe) {
    await bot.equip(stonePickaxe, "hand");
    bot.chat("Equipped stone pickaxe.");
  } else if (ironPickaxe) {
    await bot.equip(ironPickaxe, "hand");
    bot.chat("Equipped iron pickaxe.");
  } else {
    bot.chat("No suitable pickaxe available.");
    return;
  }

  // Step 4: Mine the coal ore blocks
  if (coalOreBlocks.length >= 3) {
    bot.chat("Found enough coal ore, mining now.");
    await mineBlock(bot, "coal_ore", 3);
    bot.chat("Mined 3 coal ore.");
  } else {
    bot.chat("Could not find enough coal ore.");
  }
}

